export const dateFormat = (date) => {
    const dateNew = new Date(date);

    const formattedDate = new Intl.DateTimeFormat('en-GB', {
    day: '2-digit',
    month: 'short',
    year: 'numeric',
    }).format(dateNew);

    return formattedDate

}
