import axios from  'axios'

const api_call = (api_url, method, body={}) => {
    if (method === 'POST') {
        axios.post(api_url, {payload : body})
        .then(res => {
            const data = res.data
            return data
        })
    }
    else {
        axios.get(api_url)
            .then(res => {
                const data = res.data
                return data
            })
    }

}

export default api_call
