import React from 'react'

const NewsCart = ({image, author, title}) => {
  return (
    <div className='w-1/2 pr-4 p-4 mx-2 relative cursor-pointer rounded-sm'>
        <img className='aspect-square ' alt='moviecard' src={ image }></img>
        <div className='w-3/4 aspect-video bottom-7 p-8 mx-3 bg-white z-50 absolute  opacity-90'>
            <h1 className='font-bold text-sm p-2 mx-3'>{title}</h1>
            <h3>{author ? `by - ${author}` : ''}</h3>
        </div>
    </div>
  )
}

export default NewsCart
