import React, { useEffect, useState } from 'react'
import { BASE_URL } from '../../utils/constants/constants'
import api_call from '../../utils/apiHelper/apiCall'
import { NEWS_CATEGORIES } from '../../utils/constants/constants'
import { home_page_api_url } from '../../utils/apiHelper/apiUrl'
import NewsCart from './NewsCart'

const CategoryWiseContent = () => {
  const [categoryWiseNews, setCategorywiseNews] = useState(null)

  const fetchCategoryWisenews = async() => {
    const API_URL = BASE_URL + home_page_api_url + '?country=in&category=business&apiKey=' + process.env.REACT_APP_API_KEY
    const articleData = await api_call(API_URL, 'GET')
    const articles = articleData?.articles
    setCategorywiseNews(articles)
  }

  useEffect(() => {
    fetchCategoryWisenews()
  }, [])


  return (
    <div>
      <div>
        <h1 className='p-2 m-2 font-bold text-3xl'>Business</h1>
        <div className='flex'>
          <div>
            {categoryWiseNews.map((category) => {
              <NewsCart image={category.urlToImage} author={category.author} title={category.title} />
            })}
          </div>
        </div>
      </div>
    </div>
  )
}

export default CategoryWiseContent
