import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { BASE_URL } from '../utils/constants/constants'
import { search_api_url } from '../utils/apiHelper/apiUrl'
import api_call from '../utils/apiHelper/apiCall'



const SearchNews = () => {
    const [articles, setArticles] = useState({})
    const { searchText } = useParams()

    const fetchArticles = async() => {
        const API_URL = BASE_URL + search_api_url + searchText + '&country=in'
        const articleData = await api_call(API_URL, 'GET')
        const articles = articleData?.articles
        setArticles(articles)
    }

    useEffect(() => {
        fetchArticles()
    }, [searchText])
  return (
    <div className='p-2 m-2'>
        <div className='flex'>
            <img className='w-48' src={articles.urlToImage} />
            <h1 className='p-2 m-2'>{articles.title}</h1>
        </div>
    </div>
  )
}

export default SearchNews
