import React, { useEffect, useState } from 'react'
import Header from '../Layer/Header'
import MainContent from './MainContent'
import CategoryWiseContent from './CategoryWiseContent'
import axios from 'axios'

const Home = () => {
  const [articles, setArticles] = useState({})


  useEffect(() => {
    const API_URL = "https://newsapi.org/v2/top-headlines?country=in&apiKey=411095a128ba4c97b8c800d32d998672"
    axios.get(API_URL)
    .then(res => {
      const data = res.data.articles
      setArticles(data)
    })
  }, [])

  return (
    <div className='body'>
      <Header />
      <div className='content z-50 '>
        <MainContent mainArticle={articles[0]} />
        <CategoryWiseContent articles={articles} />
      </div>
    </div>
  )
}

export default Home
